#!/usr/bin/env python
# -*- coding: UTF-8 -*-


from win32api import MessageBox
from utils.timeutils import getNowTimeString

import winsound
from utils.fileutils import createFile
from utils.fileutils import writeBase64BytesOutToFile
from threading import Thread
from utils.log import Log
from os.path import exists
from os.path import isfile

"""
Msg类用于提醒消息
"""


class MsgDingThread(Thread):
    """
    新开线程使提示音非阻塞。
    """

    def __init__(self, dingLoop):
        self.dingLoop = dingLoop
        Thread.__init__(self)

    dingLoop = 0

    def ding(self, loop=3):
        for i in range(loop):
            winsound.PlaySound(Msg.soundFilePath, flags=winsound.SND_FILENAME)

    def run(self):
        self.ding(loop=self.dingLoop)


class MsgBoxThread(Thread):
    """
    新开线程使msgBox非阻塞。
    """

    def __init__(self, text, title):
        self.text = text
        self.title = title
        Thread.__init__(self)

    text = ""
    title = ""

    def run(self):
        MessageBox(0, self.text, self.title)


class Msg:
    def __init__(self):

        raise Exception("Msg类不需要被实例化")

    from utils.fileutils import Path
    soundFilePath = Path.getProjectPath("data/ding.wav")

    @classmethod
    def _getDingWavTemplate(cls):
        """
        :return:返回一个 默认提示音文件 的base64编码。
        """
        return b''

    @classmethod
    def initDingWav(cls, soundFilePath=None):
        """
        初始化音频文件路径
        :param soundFilePath:传入路径（Path.getProjectPath("路径") ），如果不存在则会在该路径下（默认为"data/ding.wav"）创建模版音频文件
        """
        if soundFilePath is not None:
            cls.soundFilePath = soundFilePath
        if not (exists(cls.soundFilePath) and isfile(cls.soundFilePath)):  # 不存在音频文件，则自行创建
            createFile(cls.soundFilePath)
            writeBase64BytesOutToFile(cls._getDingWavTemplate(), cls.soundFilePath)

    @classmethod
    def _msg(cls, text, title, dingLoop=3):
        """
        调用该方法跳出提示框、发出提示音。
        :param text:提示框正文
        :param title:提示框标题
        :param dingLoop:提示音重复次数，不传参默认为3
        """
        MsgBoxThread(text, title).start()
        MsgDingThread(dingLoop).start()

    @classmethod
    def alarmClockMessage(cls, alarmClockObject):
        """
        闹钟提示
        :param alarmClockObject: 传入闹钟信息
        """
        text = "闹钟响了"
        if alarmClockObject is not None:
            text = "当前时间：" + getNowTimeString() + "\n闹钟：" + alarmClockObject[2]
            Log.message("闹钟 " + alarmClockObject[2] + " 生效")
        else:
            Log.message("闹钟生效")
        cls._msg(text, "TcsnHelper闹钟")

    @classmethod
    def reminderMessage(cls, reminderObject):
        """
        事件提醒
        :param reminderObject: 传入事件提醒信息
        """
        text = "事件提醒："
        if reminderObject is not None:
            text = "当前时间：" + getNowTimeString() + "\n事件提醒：" + reminderObject[1]
            Log.message("事件提醒 " + reminderObject[1])
        else:
            Log.message("事件提醒 ")
        cls._msg(text, "TcsnHelper事件提醒")

    @classmethod
    def birthdayMessage(cls, birthdayObject, preRemind=0):
        text = "生日提醒：" + birthdayObject[1] + "----"
        if birthdayObject[0][0] != 'unknown':
            text += str(birthdayObject[0][0]) + "年"
        text += str(birthdayObject[0][1]) + "月" + str(birthdayObject[0][2]) + "日" + '\n'
        if preRemind == 0:
            text += "就在今天!"
        else:
            text += "就在" + str(preRemind) + "天后。"
        cls._msg(text, "TcsnHelper生日提醒")
